      <div>
        {error && <h1>{error.message}</h1>}
        {!pokemonName && <div>Введите имя покемона</div>}
        {loading && <div>Загружаем вашего покемона...</div>}
        {pokemon && <div>{pokemon.name}</div>}
      </div>
    );
  }
}
