function fetchPokemon(name) {
  fetch(`https://pokeapi.co/api/v2/pokemon/${name}`).then((response) => {
    if (response.ok) {
      return response.json();
    }
    return Promise.reject(new Error("Упс...ничего не найдено"));
  });
}

const api = {
  fetchPokemon,
};

export default api;
