import { Component } from "react";
export class App extends Component {
  state = {
    pokemonName: null,
  };
  componentDidMount() {
    fetch(`https://pokeapi.co/api/v2/pokemon/ditto`)
      .then((response) => {
        if (response.ok) {
          return response.json();
        }
      })
      .then((pokemonName) => this.setState({ pokemonName }));
  }
  render() {
    const { pokemonName } = this.state;
    return <div>{pokemonName && <div>Тут будет покемон</div>}</div>;
  }
}
