export function fetchPokemon(pokemonName) {
  fetch(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`).then((response) => {
    if (response.ok) {
      return response.json();
    }
    return Promise.reject(new Error("Упс...ничего не найдено"));
  });
}
